// Create a map of intent slots grouped by slotName.
// Only include those we are at least 40% confident.
// Elements are arrays of slots to deal with mulltiples.
// e.g. {"switchLocation": [{"rawValue": "bedroom"}, "rawValue": "office"}]
// It is up to downstream to decide if it can deal with any multiples or
// insuficient slots.
//
// Check customData to see if this is a continued turn i.e. we got some slots
// before and we are filling in.
const slots = msg.payload["slots"];
const customData = msg.payload["customData"]
const continuedIntent = customData !== null;
// TODO: This will merge slots if intent is changed during a contintueSession
node.status({fill:"grey", shape:"dot", text: "continued? " + continuedIntent});
var by_name = {};
if (continuedIntent === true) {
    by_name = JSON.parse(customData);
}
for (var i in slots) {
    slot = slots[i];
    if (slot.confidenceScore >= 0.40) {
        list = by_name[slot.slotName] || [];
        list.push(slot);
        by_name[slot.slotName] = list;
    }
}

msg.intent.slots_by_name = by_name;
return msg;
